import React, {useState} from "react";
import CategoryCard from "../shopPages/categoryCard";

const CssClasses = {
    SLIDER: "slider",
    LIST: "navigation__list",
    ITEM: "navigation--item"
}

const Slider = ({ array }) => {
    const [index, setIndex] = useState([0,1,2])
    let importedArray = [...array]
    let sliced = []

    index.map((i) => {
        sliced.push(importedArray[i])
    })
    const [sliders, setSliders] = useState(sliced)

    function handleIncrement() {
        sliced = []
        let newIndexes = []
        index.map((i) => {
            console.log(i, importedArray.length)

            if (i === importedArray.length) {
                console.log(i, importedArray.length)
                newIndexes.push(0)
                sliced.push(importedArray[0])
            } else {
                newIndexes.push(i+1)
                sliced.push(importedArray[i+1])
            }
        })
        setIndex(newIndexes)
        setSliders(sliced)
        console.log(newIndexes)

    }



    return (
        <div className={CssClasses.SLIDER}>
            <button>←</button>
            {sliders.map((item) =>
                <CategoryCard item={item}/>
            )}
            <button onClick={handleIncrement}>→</button>
        </div>
    );
};

export default Slider;
