import React from "react";
import {useCart} from "../../hooks/useCart";
import {useSelector} from "react-redux";
import {getCurrentUserData, getIsLoggedIn} from "../../store/users";
import {getAdmins} from "../../store/admin";
import {Navigate} from "react-router-dom";
import {getItems} from "../../store/items";
import {getCategories} from "../../store/categories";

const CssClasses = {
    ADMIN: "admin-page",
    TITLE: "title",
    SUBTITLE: "subtitle",
    LIST: "about-page__list"
}

const Admin = () => {
    const currentUser = useSelector(getCurrentUserData());
    const admins = useSelector(getAdmins());
    const items = useSelector(getItems());
    const categories = useSelector(getCategories());
    let isAdmin

    if (currentUser) {
        let result = admins.find((a) => a.email === currentUser.email);
        isAdmin = result ? true : false
    }

    if (!isAdmin) {
        return <Navigate to="/" />
    }
    return (
        <div className={CssClasses.ADMIN}>
            <div className="container">

                <div>
                    <h1 className="title">Products list</h1>
                    <p className="subtitle">Totally {items.length} items</p>
                    {items.map((i) => <h1>{i.name}</h1>
                    )}
                </div>

                 <div>
                    <h1 className="title">Categories list</h1>
                    {categories.map((c) => <h1>{c.name}</h1>
                    )}
                 </div>

            </div>
        </div>


    );
};

export default Admin;
