import React, { useEffect, useState } from "react";
import { validator } from "../../utils/validator";
import TextField from "../common/form/textField";
import { useDispatch, useSelector } from "react-redux";
import { getAuthError, login } from "../../store/users";
import {useNavigate} from "react-router-dom";
import * as yup from 'yup';

const CssClasses = {
    TITLE: "title",
    BTN_VALID: "button-submit",
    BTN_INVALID: "button-submit invalid-form",
    DANGER: "auth_page__text-danger",
    CENTER: "button-wrapper--center"
}

const LoginForm = ({path}) => {
    const navigate = useNavigate()
    const dispatch = useDispatch();
    const loginError = useSelector(getAuthError());
    const [errors, setErrors] = useState({});
    const [data, setData] = useState({
        email: "",
        password: ""
    });

    const handleChange = (target) => {
        setData((prevState) => ({
            ...prevState,
            [target.name]: target.value
        }));
    };

    const validatorConfig = {
        email: { isRequired: { message: "Please enter email address" }, isEmail: { message: "Email address is invalid" } },

        password: {
            isRequired: {
                message: "Please enter password"
            }
        }
    };

    const validateScheme = yup.object().shape({
        email: yup.string()
            .required("Please enter email address")
        // password.yup
    })

    const validate = () => {
        const errors = validator(data, validatorConfig);
        validateScheme
            .validate(data)
            .then(() => setErrors({}))
        setErrors(errors);
        return Object.keys(errors).length === 0;
    };

    useEffect(() => {
        validate();
    }, [data]);

    const isValid = Object.keys(errors).length === 0;

    const handleSubmit = (e) => {
        e.preventDefault();
        const isValid = validate();
        if (!isValid) return;
        dispatch(login({ payload: data }));
        navigate(path, {replace: true})
    };
    return (
        <div>
            <h2 className={CssClasses.TITLE}>Login</h2>

            <form onSubmit={handleSubmit}>
            <TextField
                label="Email"
                name="email"
                value={data.email}
                onChange={handleChange}
                error={errors.email}
            />
            <TextField
                label="Password"
                type="password"
                name="password"
                value={data.password}
                onChange={handleChange}
                error={errors.password}
            />

            {loginError && <p className={CssClasses.DANGER}>{loginError}</p>}
                <div className={CssClasses.CENTER}>
                    <button
                        className={isValid ?  CssClasses.BTN_VALID: CssClasses.BTN_INVALID}
                        type="submit"
                    >
                        Log In
                        <i className="bi bi-box-arrow-in-right"></i>
                    </button>
                </div>
        </form>
        </div>
    );
};

export default LoginForm;
