import React, {useState} from "react";
import CategoryCard from "../shopPages/categoryCard";

const CssClasses = {
    SLIDER: "slider",
    LIST: "navigation__list",
    ITEM: "navigation--item"
}

const Slider = ({ array }) => {
    let importedArray = [...array]
    let sliced = importedArray.slice(0,3)
    const [sliders, setSliders] = useState(sliced)

    console.log(importedArray.slice(0,3))


    // setSliders(importedArray.slice(0,4))


    return (
        <div className={CssClasses.SLIDER}>
            <button>←</button>
            {sliders.map((item) =>
                <CategoryCard item={item}/>
            )}
            <button>→</button>
        </div>
    );
};

export default Slider;
