import React, { useState } from "react";
import Modal from 'react-modal';
import TextField from "./common/form/textField";

const customStyles = {
    content: {
        top: '50%',
        left: '50%',
        right: 'auto',
        bottom: 'auto',
        marginRight: '-50%',
        transform: 'translate(-50%, -50%)',
        padding: "50px",
        background: "#efefef",
        border: "2px solid #8f8f8f"
    },
};

const CssClasses = {
    BUTTON: "orders-list__button--close",
}

const AdminModal = ({modalIsOpen, closeModal, item}) => {
    const isProduct = item.price ? true : false
    const categoryData = ["id", "name", "url"]
    const productData = ["name", "url", "category", "description", "price"]
    const [data, setData] = useState(item);


    function handleCloseModal() {
        closeModal(false)
    }

    return (
            <Modal isOpen={modalIsOpen}
                   onRequestClose={closeModal}
                   style={customStyles}
            >
                <form>

                    {isProduct ? productData.map((p) => {

                        return <TextField
                            label="Name"
                            name="data.p"
                            value={data.p}
                            // onChange={handleChange}
                            // error={errors.p}
                        />
                    }) : ""

                    }



                    <button
                        type="submit"
                        // className={isValid ?  CssClasses.BTN_VALID: CssClasses.BTN_INVALID}
                    >
                        Save
                    </button>
                    </form>


                {/*<p>{item.name}</p>*/}
                {/*{item.id && <p>{item.id}</p>}*/}
            </Modal>



    );
};

export default AdminModal;
