import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

export default function SimpleSlider({array}) {
    let settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1
    };
    return (
        <Slider {...settings}>
            {array.map((item) =>
                <div>
                    <h3>{item.name}</h3>
                    <img src="https://sun9-49.userapi.com/impg/kIZW1s9OOrs8rq6X6A-3fZ9R6D5JdrIPCeJGVw/ZYecSxOoilY.jpg?size=2560x1440&quality=95&sign=0280504ec3069da4bad8b86312a5317f&type=album" alt=""/>
                </div>
            )}
        </Slider>
    );
}
