import React, { useEffect, useState } from "react";
import { validator } from "../../utils/validator";
import TextField from "../common/form/textField";
import CheckBoxField from "../common/form/checkBoxField";
import { useDispatch } from "react-redux";
import { signUp } from "../../store/users";
import {useNavigate} from "react-router-dom";
import * as yup from 'yup';

const CssClasses = {
    TITLE: "title",
    BTN_VALID: "button-submit",
    BTN_INVALID: "button-submit invalid-form",
    CENTER: "button-wrapper--center"
}

const RegisterForm = () => {
    const dispatch = useDispatch();
    const navigate = useNavigate()
    const [data, setData] = useState({
        name: "",
        surname: "",
        email: "",
        phone: "",
        password: "",
        address: "",
        sale: 0,
        licence: false
    });

    const [errors, setErrors] = useState({});
    const handleChange = (target) => {
        setData((prevState) => ({
            ...prevState,
            [target.name]: target.value
        }));
    };

    const validateScheme = yup.object().shape({
        password: yup.string()
            .required("Please enter password"),
        email: yup.string()
            .required("Please enter email address")
            .email("Email address is invalid"),
    })

    const validatorConfig = {
        email: {
            isRequired: {
                message: "Please enter email"
            },
            isEmail: {
                message: "Email address is invalid"
            }
        },
        phone: {
            isRequired: {
                message: "Please enter phone number"
            }
        },
        name: {
            isRequired: {
                message: "Please enter your name"
            }
        },
        surname: {
            isRequired: {
                message: "Please enter your surname"
            }
        },
        password: {
            isRequired: {
                message: "Please enter password"
            },
            isCapitalSymbol: {
                message: "Password must contain an upper case letter"
            },
            isContainDigit: {
                message: "Password must contain numbers"
            },
            min: {
                message: "Password must contain 8 characters or more",
                value: 8
            }
        },
        licence: {
            isRequired: {
                message:
                    "Please agree password"
            }
        }
    };

    const validate = () => {
        validateScheme
            .validate(data)
            .then(() => setErrors({}))
            .catch((err) => setErrors({[err.path]: err.message}))
        return Object.keys(errors).length === 0;
    };

    useEffect(() => {
        validate();
    }, [data]);

    const isValid = Object.keys(errors).length === 0;

    const handleSubmit = (e) => {
        e.preventDefault();
        const isValid = validate();
        if (!isValid) return;
        const newData = {...data};
        dispatch(signUp(newData));
        navigate('/shop', {replace: true})
    };

    return ( <div>
        <h2 className={CssClasses.TITLE}>Registration</h2>

        <form onSubmit={handleSubmit}>
            <TextField
                label="Name"
                name="name"
                value={data.name}
                onChange={handleChange}
                error={errors.name}
            />
            <TextField
                label="Surname"
                name="surname"
                value={data.surname}
                onChange={handleChange}
                error={errors.surname}
            />
            <TextField
                label="Email"
                name="email"
                value={data.email}
                onChange={handleChange}
                error={errors.email}
            />
            <TextField
                label="Phone"
                type="phone"
                name="phone"
                value={data.phone}
                onChange={handleChange}
                error={errors.phone}
            />
            <TextField
                label="Password"
                type="password"
                name="password"
                value={data.password}
                onChange={handleChange}
                error={errors.password}
            />
            <CheckBoxField
                value={data.licence}
                onChange={handleChange}
                name="licence"
                error={errors.licence}
            >
                Agree to our <a>Terms of Service </a> and confirm that you have read our <a>Privacy Policy</a>.
            </CheckBoxField>
            <div className={CssClasses.CENTER}>
                <button
                    className={isValid ?  CssClasses.BTN_VALID: CssClasses.BTN_INVALID}
                    type="submit"
                    disabled={!isValid}
                >
                    Submit
                </button>
            </div>
        </form>
        </div>
    );
};

export default RegisterForm;
