import React, { useState } from "react";
import Modal from 'react-modal';
import TextField from "./common/form/textField";

const customStyles = {
    content: {
        top: '50%',
        left: '50%',
        right: 'auto',
        bottom: 'auto',
        marginRight: '-50%',
        transform: 'translate(-50%, -50%)',
        padding: "50px",
        background: "#efefef",
        border: "2px solid #8f8f8f",
        width: "800px"
    },
};

const AdminModal = ({modalIsOpen, closeModal, item}) => {
    const isProduct = item.price ? true : false
    const categoryData = ["id", "name", "url"]
    const productData = ["name", "url", "category", "description", "price"]
    const [data, setData] = useState(item);

    const handleChange = (target) => {
        setData((prevState) => ({
            ...prevState,
            [target.name]: target.value
        }));
    };
    function handleCloseModal() {
        closeModal(false)
    }

    return (
            <Modal isOpen={modalIsOpen}
                   onRequestClose={closeModal}
                   style={customStyles}
            >
                <h1 style={{fontSize: "40px"}}>Edit</h1>
                <form>

                    {isProduct ? productData.map((p) => {
                        return <TextField
                            label={p}
                            name="name"
                            value={data[p]}
                            onChange={handleChange}
                            // error={errors.p}
                        />
                    }) : ""

                    }



                    <button
                        type="submit"
                        // className={isValid ?  CssClasses.BTN_VALID: CssClasses.BTN_INVALID}
                    >
                        Save
                    </button>
                    </form>


                {/*<p>{item.name}</p>*/}
                {/*{item.id && <p>{item.id}</p>}*/}
            </Modal>



    );
};

export default AdminModal;
