import React, { useEffect, useState } from "react";
import {useSelector} from "react-redux";
import {getCurrentUserData} from "../../store/users";
import LoginForm from "../authPages/loginForm";

const CssClasses = {
    MODAL: "cart-modal",
    SUBTITLE: "cart-modal__title",
    AUTH: "cart-modal__auth"
}

const CartModalTexts = {
    UNAUTHORIZED: "To place an order, you need to log in to the system",
    SUBTITLE: "Voluptate nulla atque minus dolores quas minima doloremque.",
    DELIVERY_TEXT: "Estimated delivery date: ",
}

const CartModal = () => {
    const start = Date.now()

    const now = new Date(start);
    const options = { weekday: 'long', month: 'long', day: 'numeric' };
    console.log(now.toLocaleDateString('en-EN', options));

    const end = start + 30000000000
    const delivery = new Date(end)

    console.log("delivery", delivery.toLocaleDateString('en-EN', options));

    const currentUser = useSelector(getCurrentUserData());

    return (
        <div className={CssClasses.MODAL}>

            {currentUser
                ?
                <div>
                    <p>{CartModalTexts.DELIVERY_TEXT}</p>
                        {currentUser.address.length
                        ?
                        <p>Address: {currentUser.address}</p>
                        :
                        <p>EDIT</p>}

                </div>

                :
                <div className={CssClasses.AUTH}>
                    <p className={CssClasses.SUBTITLE}>{CartModalTexts.UNAUTHORIZED}</p>
                    <LoginForm path={"/cart"}/>
                </div>
            }


        </div>
    );
};

export default CartModal;
