import React from "react";
import { useSelector } from "react-redux";
import {getCurrentUserData} from "../../store/users";
import {Link, NavLink, useLocation, useNavigate} from "react-router-dom";

const Profile = () => {
    const currentUser = useSelector(getCurrentUserData());
    const navigate = useNavigate();
    const location = useLocation();



    console.log(currentUser)

    const handleClick = () => {
        // history.push(history.location.pathname + "/edit");
        navigate(currentUser._id + "/edit");
    };

    if (currentUser) {
        return (
            <div className="profile">
                <div className="container">
                    <div className="profile-info">
                        <h1>{currentUser.name + " " + currentUser.surname}</h1>

                        <h3><span>Your sale:</span>  {currentUser.sale}%</h3>
                        <div className="sale-info">
                            For every 1000 rubles in the order, you get +1% to the cumulative discount for the next orders. The maximum discount under the accumulative program is 15%.
                        </div>
                        <div className="contact-info">
                            <h4>Contact information</h4>
                            <ul>
                                <li><span>Phone:</span> {currentUser.phone}</li>
                                <li><span>Birth date:</span> {currentUser.address ? currentUser.address : <NavLink>add</NavLink>}</li>
                                <li><span>Address:</span> {currentUser.address ? currentUser.address : <NavLink>add</NavLink>}</li>
                            </ul>

                            <button onClick={handleClick}>Edit contact information</button>
                        </div>
                    </div>

                    <div>
                        <div>
                            <h2>Your orders</h2>
                            { currentUser.orders
                                ? <div>
                                    <h3>Orders</h3>
                                    <ul>

                                    </ul>
                                </div>
                                : <p>You have no orders! Start <Link to="/shop">shopping</Link> now.</p>
                            }
                        </div>
                    </div>



                </div>
            </div>
        );
    } else {
        return <h1>Loading</h1>;
    }
};

export default Profile;
