import React, {useEffect} from "react";
import './sass/main.scss';
import {Navigate, Route, Routes} from 'react-router-dom';
import Home from './components/layouts/home';
import About from './components/layouts/about';
import Contact from './components/layouts/contact';
import Cart from './components/cartPage/cart';
import NavBar from './components/common/navBar';
import { CartProvider } from './hooks/useCart';
import AppLoader from "./hoc/appLoader";
import LogOut from "./components/AuthLayout/logOut";
import Footer from "./components/common/footer";
import ItemPage from "./components/ShopLayout/itemPage";
import ShopPage from "./components/ShopLayout/shopPage";
import RegisterForm from "./components/AuthLayout/registerForm";
import LoginForm from "./components/AuthLayout/loginForm";
import ProfileInfo from "./components/ProfileLayout/profileInfo";
import ProfileEdit from "./components/ProfileLayout/editForm";
import AdminLayout from "./components/AdminLayout/adminLayout";
import {useDispatch, useSelector} from "react-redux";
import {getCurrentUserData, getCurrentUserId, getIsLoggedIn} from "./store/users";
import {getItems} from "./store/items";
import {getCategories} from "./store/categories";
import {getAdmins} from "./store/admin";

function App() {
    const isLoggedIn = useSelector(getIsLoggedIn());
    const id = useSelector(getCurrentUserId());
    const dispatch = useDispatch();

    useEffect(() => {
        if (isLoggedIn) {
            dispatch(getItems());
            dispatch(getCategories());
            dispatch(getCurrentUserData());
            dispatch(getAdmins());
        }
    }, [isLoggedIn, dispatch]);


  return (
      <div className="body">
          <CartProvider>
            <AppLoader>

            <NavBar/>
            <Routes>
                <Route index element={<Home />}/>
                <Route path="about" element={<About />}/>
                <Route path="/contact" element={<Contact />}/>
                <Route path="/cart" element={<Cart />}/>
                <Route path="/admin" element={<AdminLayout />}/>

                <Route path="shop">
                    <Route index element={<ShopPage />}/>
                    <Route path=":itemId" element={<ItemPage/>}/>
                </Route>

                <Route path="auth">
                    <Route index element={<Navigate to="/auth/login"/>}/>
                    <Route path="register" element={<RegisterForm />}/>
                    <Route path="login" element={<LoginForm />}/>
                    <Route path="logout" element={<LogOut />} />
                    <Route path="*" element={<Navigate to="/auth/login"/>}/>
                </Route>

                <Route path="profile" >
                    <Route index element={<ProfileInfo />}/>
                    <Route path="edit" element={<ProfileEdit />}/>
                    <Route path="*" element={<Navigate to="/profile"/>}/>
                </Route>

                <Route path="*" element={<Navigate to="/" />}/>

            </Routes>
            </AppLoader>

          </CartProvider>
          <Footer/>
      </div>

  );
}

export default App;
