import React, { useContext, useEffect, useState } from "react";

const CartContext = React.createContext()
export const useCart = () => {
    return useContext(CartContext)
}
export const CartProvider = ({children}) => {
    const [cart, setCart] = useState([])
    //
    // useEffect(() => {
    //     const cartValue = localStorage.getItem("cart")
    //     if (cartValue) {
    //         const cartStorage = JSON.parse(cartValue)
    //         setCart(cartStorage)
    //     }
    // }, []);

    // console.log(cart[0].item)

    const addCartItem = (data) => {
        const local = localStorage.getItem("cart")

        if (local) {
            const cartSet = JSON.parse(local)
            let counter = 0

            for (let i =0; i <cartSet.length; i++) {
                if (cart[0].item === data) {
                    counter++
                }
            }
            const cartData = {'item': data, 'counter':counter}
            cartSet.push(cartData)
            localStorage.setItem("cart", JSON.stringify(cartSet))

        } else {
            let local = []
            const cartData = {'item': data, 'counter':0}
            setCart(prevState => [...prevState, cartData])
            local.push(cartData)
            localStorage.setItem("cart", JSON.stringify(local))
        }
    }

    const clearCart = () => {
        setCart([])
        localStorage.removeItem("cart")
    }

    return (
    <CartContext.Provider value={{cart, addCartItem, clearCart}}>
        {children}
    </CartContext.Provider>)
}
