import React from "react";
import Slider from "react-slick";

export default function SimpleSlider({array}) {
    let settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1
    };
    return (
        <Slider {...settings}>
            {array.map((item) =>
                <div>
                    <h3>{item.name}</h3>
                </div>
            )}
        </Slider>
    );
}
