                    <h2>Totaly</h2>
                    <div className="cart__container--price">
                        <p>{cart.length} items</p>
                        <p>1440 â‚½</p>
                    </div>
                    <button className="checkout_cart">Checkout</button>
                    <div>
                        <button onClick={handleClearCart} className="clear_cart">clear cart</button>
