                </div>

            </div>
        </>
        : <>
            <h1 className="cart__title">Your cart is empty</h1>
            <p className="cart__subtitle">Navigate to <Link to="/react-e-commerce/shop">shop</Link> or <Link to="/react-e-commerce/">home</Link></p>
        </>
    }

    </div>
    );
};

export default Cart;
