import React, { useContext, useEffect, useRef, useState } from "react";
// import { toast } from "react-toastify";
// import qualityService from "../services/quality.service";

const CartContext = React.createContext()
export const useCart = () => {
    return useContext(CartContext)
}
export const CartProvider = ({children}) => {
    const [cart, setCart] = useState([])

    useEffect(() => {
        const cartValue = localStorage.getItem("cart")
        if (cartValue) {
            const cartStorage = JSON.parse(cartValue)
            setCart(cartStorage)
        }
    }, []);

    const addCartItem = (data) => {
        setCart(prevState => [...prevState, data])

        const local = localStorage.getItem("cart")

        if (local) {
            const cartSet = JSON.parse(local)
            cartSet.push(data)
            localStorage.setItem("cart", JSON.stringify(cartSet))

        } else {
            let local = []
            local.push(data)
            localStorage.setItem("cart", JSON.stringify(local))
        }
    }

    const clearCart = () => {
        setCart([])
        localStorage.removeItem("cart")
    }

    return (
    <CartContext.Provider value={{cart, addCartItem, clearCart}}>
        {children}
    </CartContext.Provider>)
}
