import React, { useState } from "react";
import Modal from 'react-modal';
import OrderCard from "./orderCard";

const customStyles = {
    content: {
        top: '50%',
        left: '50%',
        right: 'auto',
        bottom: 'auto',
        marginRight: '-50%',
        transform: 'translate(-50%, -50%)',
        // border: 'none',
        padding: "50px",
        height: "600px",
        background: "#efefef",
        border: "2px solid black"
    },
};

const CssClasses = {
    MODAL: "cart-modal",
    TITLE: "cart-modal__title",
    AUTH: "cart-modal__auth",
    INFO:"cart-modal__auth-info",
    LOGIN: "cart-modal__auth-login",
    SUBTITLE: "cart-modal__subtitle",
    DATA: "cart-modal__data",
    RESULT: "cart-modal__result",
    RADIO: "cart-modal__radio",
    CHECKOUT: "button-submit",
    PRICE: "cart-modal__result-price",
}

const OrdersListModal = ({modalIsOpen, closeModal, orders}) => {

    //     closeModal(true)


    return (
        <Modal isOpen={modalIsOpen}
               onRequestClose={closeModal}
               style={customStyles}
        >
            <ul className={CssClasses.LIST}>
                {orders.map((data) => (
                    <li><OrderCard data={data}/></li>
                ))}
            </ul>
        </Modal>

    );
};

export default OrdersListModal;
